import Welcome from "./Welcome";

function getGreeting(user) {
    if (user) {
    // jika user berisi
    return <h1>Hello, {formatName(user)}!</h1>;
    }
    // jika user kosong
    return <h1>Hello, Stranger.</h1>;
    }
function App(){
    const user = "Afidatul";
    return(
        <div className="App">
            <Welcome nama={user}/>
            
        </div>
    );
}

export default App
